import React from "react";
import Tag from "./Tag";

export default function useTag(){
    const tagState = Tag.getInstance();

    const [state, setState] = React.useState(tagState.getState());
    console.log(state);
    function newGame () {
        tagState.startNewGame();
        setState(tagState.getState());
    }

    function undo () {
        tagState.undo();
        setState(tagState.getState());
    }

    function move (i) {
        return function () {
        tagState.moveTile(i);
        setState(tagState.getState());
        }
    }

    React.useEffect(() => {
        document.addEventListener('keyup', function listeners (event) {

        if (event.code === 37) tagState.moveInDirection('left');
        else if (event.code === 38) tagState.moveInDirection('up');
        else if (event.code === 39) tagState.moveInDirection('right');
        else if (event.code === 40) tagState.moveInDirection('down');

        setState(tagState.getState());
        });

        return (() => window.removeEventListener());
    }, [tagState]); 

    return [state.seconds, state.board, state.moves, state.solved, newGame, undo, move];
}
